import { ButtonComponent } from './button.component';
// More on how to set up stories at: https://storybook.js.org/docs/writing-stories
const meta = {
    title: 'Sandox/Button',
    component: ButtonComponent,
    tags: ['autodocs'],
    render: (args) => ({
        props: {
            backgroundColor: null,
            ...args,
        },
    }),
    argTypes: {
        backgroundColor: {
            control: 'color',
        },
    },
};
export default meta;
const image = {
    src: imageFile,
    alt: 'my image',
};
// More on writing stories with args: https://storybook.js.org/docs/writing-stories/args
export const Filled = {
    args: {
        style: 'filled',
        state: 'enabled',
        size: 'large',
        label: 'Filled button',
        iconUrl: 'assets/icon.svg',
    },
};
export const Outlined = {
    args: {
        style: 'outlined',
        state: 'enabled',
        size: 'large',
        label: 'Outlined button',
    },
};
export const Text = {
    args: {
        style: 'text',
        state: 'enabled',
        size: 'large',
        label: 'Text button',
    },
};
export const Elevated = {
    args: {
        style: 'elevated',
        state: 'enabled',
        size: 'large',
        label: 'Elevated button',
    },
};
export const Tonal = {
    args: {
        style: 'tonal',
        state: 'enabled',
        size: 'large',
        label: 'Tonal button',
    },
};
