import { NgModule } from "@angular/core";
import { CommonModule } from "@angular/common";
import { Component } from "@angular/core";

@Component({
  selector: "my-component, MyComponent",
  template: `
    <header class="main-container">
      <main class="image-wrapper">
        <section class="description">
          <div class="column">
            <span class="span">
              <div class="title">Plant trees for free</div>
              <div class="subtitle">Make the planet better</div>
            </span>
          </div>
          <div class="column">
            <span class="span">
              <div class="input-wrapper">
                <input type="text" class="input" aria-label="Email" />
                <label for="email">Email</label>
              </div>
              <div class="input-wrapper">
                <input type="password" class="input" aria-label="Password" />
                <label for="password">Password</label>
              </div>
              <a href="#" class="login-link">Login</a>
              <div class="subtitle">Otherwise, please</div>
              <div class="subtitle">Register here</div>
            </span>
          </div>
        </section>
      </main>
    </header>
  `,
  styles: [
    `
      .main-container {
        justify-content: center;
        align-self: stretch;
        background-color: var(--whitePure, #081c15);
        display: flex;
        max-width: 955px;
        flex-direction: column;
        padding: 50px 16px;
      }

      .image-wrapper {
        margin: 160px 0 92px;
      }

      @media (max-width: 991px) {
        .image-wrapper {
          max-width: 100%;
          margin: 40px 0;
        }
      }

      .description {
        gap: 20px;
        display: flex;
      }

      @media (max-width: 991px) {
        .description {
          flex-direction: column;
          align-items: stretch;
          gap: 0px;
        }
      }

      .column {
        display: flex;
        flex-direction: column;
        line-height: normal;
        width: 55%;
        margin-left: 0px;
      }

      @media (max-width: 991px) {
        .column {
          width: 100%;
        }
      }

      .span {
        align-self: stretch;
        display: flex;
        flex-direction: column;
        margin: auto 0;
      }

      @media (max-width: 991px) {
        .span {
          max-width: 100%;
          margin-top: 40px;
        }
      }

      .title {
        color: var(--greenDark, #e8f5f1);
        font: 500 24px Poppins, sans-serif;
      }

      @media (max-width: 991px) {
        .title {
          max-width: 100%;
        }
      }

      .subtitle {
        color: var(--greenDark, #e8f5f1);
        margin-top: 8px;
        font: 500 24px Poppins, sans-serif;
      }

      @media (max-width: 991px) {
        .subtitle {
          max-width: 100%;
        }
      }

      .input-wrapper {
        border-radius: var(--8, 8px);
        background-color: var(--greenLight, #d0ffd7);
        display: flex;
        margin-top: 32px;
        flex-direction: column;
        justify-content: center;
      }

      .input {
        justify-content: center;
        align-items: center;
        border-radius: var(--4, 4px);
        border: 1px solid var(--greenLight, #d0ffd7);
        background-color: var(--greenLight, #d0ffd7);
        display: flex;
        height: 40px;
        flex-direction: column;
        padding: 0 20px;
      }

      .login-link {
        color: var(--cardsWhite, #081c15);
        white-space: nowrap;
        justify-content: center;
        align-items: center;
        border-radius: 6px;
        box-shadow: 0px 4px 61px 0px rgba(45, 106, 79, 0.4);
        background-color: var(--cardsMainColor, #f4fbf9);
        margin-top: 32px;
        padding: 11px 60px;
        font: 600 18px Poppins, sans-serif;
      }

      @media (max-width: 991px) {
        .login-link {
          white-space: initial;
          padding: 0 20px;
        }
      }
    `,
  ],
})
