import { Component, NgZone } from "@angular/core";

@Component({
  selector: "my-component, MyComponent",
  template: `
    <span class="span">
      <div class="div">Which ads are you interested in?</div>
      <div class="div-2">Health and Fitness</div>
      <div class="div-3">
        <span class="span-2" [class.active]="isSelected(2)" (click)="toggleChip(2)">
          {{ getChipLabel(2) }}
        </span>
        <span class="span-3" [class.active]="isSelected(3)" (click)="toggleChip(3)">
          {{ getChipLabel(3) }}
        </span>
        <span class="span-4" [class.active]="isSelected(4)" (click)="toggleChip(4)">
          {{ getChipLabel(4) }}
        </span>
        <span class="span-5" [class.active]="isSelected(5)" (click)="toggleChip(5)">
          {{ getChipLabel(5) }}
        </span>
      </div>

      <div class="div-39">
        <span class="span-99" [class.active]="isSelected(99)" (click)="toggleChip(99)">
          {{ getChipLabel(99) }}
        </span>
        <span class="span-100" [class.active]="isSelected(100)" (click)="toggleChip(100)">
          {{ getChipLabel(100) }}
        </span>
        <span class="span-101" [class.active]="isSelected(101)" (click)="toggleChip(101)">
          {{ getChipLabel(101) }}
        </span>
      </div>
      <span class="span-102">Watch videos</span>
    </span>
  `,
  styles: [`
    .active {
      background-color: #007bff; 
      color: #ffffff; 
    }

    .active::after {
      content: '\u2713'; 
      margin-left: 5px; 
    }
  `]
})
export class MyComponent {
  selectedChips: number[] = [];

  constructor(private ngZone: NgZone) {}

  toggleChip(chipNumber: number): void {
    const index = this.selectedChips.indexOf(chipNumber);
    if (index !== -1) {
      this.selectedChips.splice(index, 1);
    } else {
      this.selectedChips.push(chipNumber);
    }

    this.ngZone.run(() => {}); // Run change detection inside the Angular zone
  }

  isSelected(chipNumber: number): boolean {
    return this.selectedChips.includes(chipNumber);
  }

  getChipLabel(chipNumber: number): string {
    const category = this.getChipCategory(chipNumber);
    return this.isSelected(chipNumber) ? `${category} \u2713` : category;
  }

  getChipCategory(chipNumber: number): string {
    // Add logic to determine the category based on chipNumber
    // You can use a switch statement, if-else conditions, or any other logic
    switch (chipNumber) {
      case 2:
        return "Gym Memberships";
      case 3:
        return "Fitness Equipment";
      case 4:
        return "Healthy Meal Delivery Services";
      case 5:
        return "Vitamins and Supplements";
      case 99:
        return "Vehicle Accessories";
      case 100:
        return "Auto Detailing Services";
      case 101:
        return "Roadside Assistance Programs";
      default:
        return `Category ${chipNumber}`;
    }
  }
}
