import { NgModule } from "@angular/core";
import { CommonModule } from "@angular/common";

import { Component } from "@angular/core";

@Component({
  selector: "my-component, MyComponent",
  template: `
    <header class="main-container">
      <div class="header">
        <div class="header-content">
          <div class="column">
            <span class="text-container">
              <div class="title">Plant trees for free</div>
              <div class="subtitle">Make the planet better</div>
            </span>
          </div>
          <div class="column">
            <span class="text-container">
              <div class="subtitle-1">Sign in</div>
              <div class="input-wrapper">
                <label for="email">Email</label>
                <input id="email" class="input" type="text">
              </div>
              <div class="input-wrapper">
                <label for="password">Password</label>
                <input id="password" class="input" type="password">
              </div>
              <button class="login-button">Login</button>
              <span class="text-container">
                <div class="description">Otherwise, please</div>
                <div class="register-link">Register here</div>
              </span>
            </span>
          </div>
        </div>
      </div>
    </header>
  `,
  styles: [
    `
      .main-container {
        justify-content: center;
        align-self: stretch;
        background-color: var(--whitePure, #081c15);
        display: flex;
        flex-direction: column;
        padding: 50px 16px;
      }

      .header {
        margin: 160px 0 92px;
      }

      @media (max-width: 991px) {
        .header {
          max-width: 100%;
          margin: 40px 0;
        }
      }

      .header-content {
        gap: 20px;
        display: flex;
      }

      @media (max-width: 991px) {
        .header-content {
          flex-direction: column;
          align-items: stretch;
          gap: 0px;
        }
      }

      .column {
        display: flex;
        flex-direction: column;
        line-height: normal;
        width: 55%;
        margin-left: 0px;
      }

      @media (max-width: 991px) {
        .column {
          width: 100%;
        }
      }

      .text-container {
        align-self: stretch;
        display: flex;
        flex-direction: column;
        margin: auto 0;
      }

      @media (max-width: 991px) {
        .text-container {
          max-width: 100%;
          margin-top: 40px;
        }
      }

      .title {
        color: var(--greenDark, #e8f5f1);
        font: 500 24px Poppins, sans-serif;
      }

      @media (max-width: 991px) {
        .title {
          max-width: 100%;
        }
      }

      .subtitle {
        color: var(--greenDark, #e8f5f1);
        margin-top: 8px;
        font: 500 24px Poppins, sans-serif;
      }

      @media (max-width: 991px) {
        .subtitle {
          max-width: 100%;
        }
      }

      .column-2 {
        display: flex;
        flex-direction: column;
        line-height: normal;
        width: 45%;
        margin-left: 20px;
      }

      @media (max-width: 991px) {
        .column-2 {
          width: 100%;
        }
      }

      .subtitle-1 {
        color: var(--greenDark, #e8f5f1);
        font-feature-settings: "clig" off, "liga" off;
        white-space: nowrap;
        font: 400 32px Poppins, sans-serif;
      }

      @media (max-width: 991px) {
        .subtitle-1 {
          white-space: initial;
        }
      }

      .input-wrapper {
        border-radius: var(--8, 8px);
        background-color: var(--greenLight, #d0ffd7);
        display: flex;
        margin-top: 32px;
        flex-direction: column;
        justify-content: center;
      }

      label {
        justify-content: center;
        color: var(--constantGreenDark, #081c15);
        white-space: nowrap;
        border-radius: var(--4, 4px);
        border: 1px solid var(--greenLight, #d0ffd7);
        background-color: var(--greenLight, #d0ffd7);
        padding: 0 2px;
        font: 400 16px Inter, sans-serif;
      }

      @media (max-width: 991px) {
        label {
          white-space: initial;
        }
      }

      .input {
        justify-content: center;
        color: var(--constantGreenDark, #081c15);
        white-space: nowrap;
        border-radius: var(--4, 4px);
        border: 1px solid var(--greenLight, #d0ffd7);
        background-color: var(--greenLight, #d0ffd7);
        padding: 0 2px;
        font: 400 16px Inter, sans-serif;
      }

      @media (max-width: 991px) {
        .input {
          white-space: initial;
        }
      }

      .login-button {
        color: var(--cardsWhite, #081c15);
        white-space: nowrap;
        justify-content: center;
        align-items: center;
        border-radius: 6px;
        box-shadow: 0px 4px 61px 0px rgba(45, 106, 79, 0.4);
        background-color: var(--cardsMainColor, #f4fbf9);
        margin-top: 32px;
        padding: 11px 60px;
        font: 600 18px Poppins, sans-serif;
      }

      @media (max-width: 991px) {
        .login-button {
          white-space: initial;
          padding: 0 20px;
        }
      }

      .text-container-2 {
        align-items: center;
        align-content: center;
        flex-wrap: wrap;
        display: flex;
        margin-top: 32px;
        justify-content: space-between;
        gap: 6px;
      }

      .description {
        color: var(--greenDark, #e8f5f1);
        margin: auto 0;
        font: 400 14px Poppins, sans-serif;
      }

      .register-link {
        color: var(--greenDark, #e8f5f1);
        font: 600 16px Poppins, sans-serif;
      }
    `,
  ],
})
export class MyComponent {}
