import { NgModule } from "@angular/core";
import { CommonModule } from "@angular/common";

import { Component } from "@angular/core";

@Component({
  selector: "my-component, MyComponent",
  template: `
  <span class="span">
  <div class="div">Which ads are you interested in?</div>
  <div class="div-2">Health and Fitness</div>
  <div class="div-3">
    <span class="span-2" [class.active]="activeChip === 2" (click)="toggleChip(2)">
      {{ getChipLabel(2) }}
    </span>
    <span class="span-3" [class.active]="activeChip === 3" (click)="toggleChip(3)">
      {{ getChipLabel(3) }}
    </span>
    <span class="span-4" [class.active]="activeChip === 4" (click)="toggleChip(4)">
      {{ getChipLabel(4) }}
    </span>
    <span class="span-5" [class.active]="activeChip === 5" (click)="toggleChip(5)">
      {{ getChipLabel(5) }}
    </span>
  </div>

  <div class="div-39">
    <span class="span-99" [class.active]="activeChip === 99" (click)="toggleChip(99)">
      {{ getChipLabel(99) }}
    </span>
    <span class="span-100" [class.active]="activeChip === 100" (click)="toggleChip(100)">
      {{ getChipLabel(100) }}
    </span>
    <span class="span-101" [class.active]="activeChip === 101" (click)="toggleChip(101)">
      {{ getChipLabel(101) }}
    </span>
  </div>
  <span class="span-102">Watch videos</span>
</span>
  `,
  styles: [
    `   .active {
      background-color: #007bff; /* Add your active background color */
      color: #ffffff; /* Add your active text color */
    }

    .active::after {
      content: '\2713'; /* Checkmark symbol */
      margin-left: 5px; /* Add spacing between label and checkmark */
    }
    `,
  ],
})
export class MyComponent {
  activeChip: number | null = null;

  toggleChip(chipNumber: number): void {
    this.activeChip = (this.activeChip === chipNumber) ? null : chipNumber;
  }

  getChipLabel(chipNumber: number): string {
    return (this.activeChip === chipNumber) ? `Active: ${chipNumber}` : `Inactive: ${chipNumber}`;
  }
}
