import { NgModule } from "@angular/core";
import { CommonModule } from "@angular/common"; 
import { Component } from "@angular/core";

@Component({ 
  selector: "my-component, MyComponent", 
  template: `
    <header class="main-container">
      <header class="image-wrapper">
        <header class="description">
          <header class="column">
            <header class="span">
              <header class="title">Plant trees for free</header>
              <header class="title">Make the planet better</header>
            </header>
          </header>
          <header class="column-2">
            <header class="span-2">
              <header class="title">Sign in</header>
              <header class="title">Username</header>
              <input class="input" placeholder="Enter username" aria-label="Username input" />
              <header class="title">Username</header>
              <input class="input" placeholder="Enter username" aria-label="Username input" />
              <button class="button">Login</button>
              <header class="title">Otherwise, please</header>
              <header class="title">Register here</header>
            </header>
          </header>
        </header>
      </header>
    </header>
  `,
  styles: [`
    .main-container {
      justify-content: center;
      align-self: stretch;
      background-color: var(--whitePure, #081c15);
      display: flex;
      max-width: 955px;
      flex-direction: column;
      padding: 50px 16px;
    }
    .image-wrapper {
      margin: 128px 0 67px;
    }
    @media (max-width: 991px) {
      .image-wrapper {
        max-width: 100%;
        margin: 40px 0;
      }
    }
    .description {
      gap: 20px;
      display: flex;
    }
    @media (max-width: 991px) {
      .description {
        flex-direction: column;
        align-items: stretch;
        gap: 0px;
      }
    }
    .column {
      display: flex;
      flex-direction: column;
      line-height: normal;
      width: 55%;
      margin-left: 0px;
    }
    @media (max-width: 991px) {
      .column {
        width: 100%;
      }
    }
    .span {
      align-self: stretch;
      display: flex;
      flex-direction: column;
      margin: auto 0;
    }
    @media (max-width: 991px) {
      .span {
        max-width: 100%;
        margin-top: 40px;
      }
    }
    .title {
      color: var(--greenDark, #e8f5f1);
      font: 500 24px Poppins, sans-serif;
    }
    @media (max-width: 991px) {
      .title {
        max-width: 100%;
      }
    }
    .column-2 {
      display: flex;
      flex-direction: column;
      line-height: normal;
      width: 45%;
      margin-left: 20px;
    }
    @media (max-width: 991px) {
      .column-2 {
        width: 100%;
      }
    }
    .span-2 {
      justify-content: center;
      display: flex;
      flex-grow: 1;
      flex-direction: column;
    }
    @media (max-width: 991px) {
      .span-2 {
        margin-top: 32px;
      }
    }
    .input {
      border-radius: var(--4, 4px);
      border: 1px solid var(--greenLight, #d0ffd7);
      background-color: var(--greenLight, #d0ffd7);
      display: flex;
      height: 40px;
      flex-direction: column;
      padding: 0 10px;
    }
    .title {
      color: var(--greenDark, #e8f5f1);
      margin-top: 16px;
      font: 500 14px Roboto, sans-serif;
    }
    .button {
      border-radius: var(--4, 4px);
      background-color: var(--greenLight, #d0ffd7);
      display: flex;
      margin-top: 16px;
      flex-direction: column;
      justify-content: center;
      padding: 0 10px;
      color: var(--greenDark, #e8f5f1);
      height: 40px;
      font: 500 14px Roboto, sans-serif;
    }
    .title {
      border-radius: var(--8, 8px);
      background-color: var(--greenLight, #d0ffd7);
      display: flex;
      margin-top: 16px;
      flex-direction: column;
      justify-content: center;
    }
    .input {
      justify-content: center;
      align-items: center;
      border-radius: var(--4, 4px);
      border: 1px solid var(--greenLight, #d0ffd7);
      background-color: var(--greenLight, #d0ffd7);
      display: flex;
      height: 40px;
      flex-direction: column;
      padding: 0 10px;
    }
    .button {
      border-radius: 6px;
      justify-content: center;
      align-items: center;
      border-radius: 6px;
      box-shadow: 0px 4px 61px 0px rgba(45, 106, 79, 0.4);
      background-color: var(--cardsMainColor, #f4fbf9);
      margin-top: 32px;
      padding: 11px 60px;
      font: 600 18px Poppins, sans-serif;
    }
    @media (max-width: 991px) {
      .button {
        white-space: initial;
        padding: 0 20px;
      }
    }
    .span-4 {
      align-items: center;
      display: flex;
      margin-top: 32px;
      justify-content: space-between;
      gap: 6px;
    }
    .title {
      color: var(--greenDark, #e8f5f1);
      margin: auto 0;
      font: 400 14px Poppins, sans-serif;
    }
    .title {
      color: var(--greenDark, #e8f5f1);
      font: 600 16px Poppins, sans-serif;
    }
  `],
})
export class MyComponent {}
