import { Component, NgModule } from "@angular/core";
import { CommonModule } from "@angular/common";

@Component({
  selector: "my-component, MyComponent",
  template: `
    <header class="main-container">
      <div class="header">
        <div class="logo"><img alt="Logo"></div>
        <nav class="nav">
          <a href="#" class="nav-link">Home</a>
          <a href="#" class="nav-link">About</a>
          <a href="#" class="nav-link">Services</a>
          <a href="#" class="nav-link">Contact</a>
        </nav>
      </div>
    </header>

    <main class="container">
      <section class="section">
        <h2 class="title">Plant trees for free</h2>
        <p class="description">Make the planet better</p>
      </section>

      <section class="section">
        <h2 class="title">Sign in</h2>
        <form class="form">
          <div class="form-group">
            <label for="email" class="label">Email</label>
            <input type="email" id="email" class="input">
          </div>

          <div class="form-group">
            <label for="password" class="label">Password</label>
            <input type="password" id="password" class="input">
          </div>

          <button class="button">Login</button>
        </form>

        <p class="alternative-text">
          Otherwise, please
          <a href="#" class="link">Register here</a>
        </p>
      </section>
    </main>

    <footer class="footer">
      <p>&copy; 2021 My Website. All Rights Reserved.</p>
    </footer>
  `,
  styles: [
    `
      .main-container {
        justify-content: center;
        align-self: stretch;
        background-color: var(--whitePure, #081c15);
        display: flex;
        max-width: 955px;
        flex-direction: column;
        padding: 50px 16px;
      }

      .header {
        display: flex;
        justify-content: space-between;
        align-items: center;
      }

      .logo {
        flex: 1;
      }

      .nav {
        display: flex;
        gap: 16px;
      }

      .nav-link {
        text-decoration: none;
        color: var(--whitePure, #081c15);
      }

      .container {
        margin: 160px 0 92px;
      }

      .section {
        gap: 20px;
        display: flex;
        flex-direction: column;
      }

      .title {
        color: var(--greenDark, #e8f5f1);
        font: 500 24px Poppins, sans-serif;
        margin: 0;
      }

      .description {
        color: var(--greenDark, #e8f5f1);
        margin-top: 8px;
        font: 500 24px Poppins, sans-serif;
      }

      .form-group {
        display: flex;
        flex-direction: column;
        margin-top: 16px;
      }

      .label {
        color: var(--greenDark, #e8f5f1);
        font-feature-settings: "clig" off, "liga" off;
        font: 400 32px Poppins, sans-serif;
        margin: 0;
      }

      .input {
        border-radius: var(--8, 8px);
        background-color: var(--greenLight, #d0ffd7);
        margin-top: 32px;
        padding: 0 2px;
        color: var(--constantGreenDark, #081c15);
        border: 1px solid var(--greenLight, #d0ffd7);
        font: 400 16px Inter, sans-serif;
      }

      .button {
        color: var(--cardsWhite, #081c15);
        white-space: nowrap;
        justify-content: center;
        align-items: center;
        border-radius: 6px;
        box-shadow: 0px 4px 61px 0px rgba(45, 106, 79, 0.4);
        background-color: var(--cardsMainColor, #f4fbf9);
        margin-top: 32px;
        padding: 11px 60px;
        font: 600 18px Poppins, sans-serif;
      }

      .alternative-text {
        align-items: center;
        display: flex;
        margin-top: 32px;
        gap: 6px;
      }

      .link {
        color: var(--greenDark, #e8f5f1);
        font: 600 16px Poppins, sans-serif;
        white-space: nowrap;
      }

      .footer {
        margin-top: 92px;
        text-align: center;
      }
    `,
  ],
})
export class MyComponent {}

