import { NgModule } from "@angular/core";
import { CommonModule } from "@angular/common";

import { Component } from "@angular/core";

@Component({
  selector: "my-component",
  template: `
    <header class="main-container">
      <div class="header">
        <div class="header-content">
          <div class="column">
            <div class="plant-trees">Plant trees for free.</div>
            <div class="make-planet">Make the planet better.</div>
          </div>
          <div class="column">
            <div class="sign-in">Sign in</div>
            <div class="username">
              <label for="username">Username</label>
              <input id="username" type="text" class="input" aria-label="username" />
            </div>
            <div class="password">
              <label for="password">PASSWORD</label>
              <input id="password" type="password" class="input" aria-label="password" />
            </div>
            <button class="login">Login</button>
            <div class="register-here">
              <div class="otherwise">Otherwise, please</div>
              <div class="register">Register here</div>
            </div>
          </div>
        </div>
      </div>
    </header>
  `,
  styles: [
    `
      .main-container {
        justify-content: center;
        align-self: stretch;
        background-color: var(--whitePure, #081c15);
        display: flex;

        flex-direction: column;
        padding: 50px 16px;
      }
      .header {
        margin: 138px 0 75px;
      }
      @media (max-width: 991px) {
        .header {
          max-width: 100%;
          margin: 40px 0;
        }
      }
      .header-content {
        gap: 20px;
        display: flex;
      }
      @media (max-width: 991px) {
        .header-content {
          flex-direction: column;
          align-items: stretch;
          gap: 0px;
        }
      }
      .column {
        display: flex;
        flex-direction: column;
        line-height: normal;
        width: 55%;
        margin-left: 0px;
      }
      @media (max-width: 991px) {
        .column {
          width: 100%;
        }
      }
      .plant-trees {
        color: var(--greenDark, #e8f5f1);
        letter-spacing: 0.25px;
        font: 400 34px/118% Roboto, sans-serif;
      }
      @media (max-width: 991px) {
        .plant-trees {
          max-width: 100%;
        }
      }
      .make-planet {
        color: var(--greenDark, #e8f5f1);
        letter-spacing: 0.25px;
        margin-top: 8px;
        font: 400 34px/118% Roboto, sans-serif;
      }
      @media (max-width: 991px) {
        .make-planet {
          max-width: 100%;
        }
      }
      .column-2 {
        display: flex;
        flex-direction: column;
        line-height: normal;
        width: 45%;
        margin-left: 20px;
      }
      @media (max-width: 991px) {
        .column-2 {
          width: 100%;
        }
      }
      .sign-in {
        color: var(--greenDark, #e8f5f1);
        white-space: nowrap;
        font: 400 23px/139% Roboto, sans-serif;
      }
      @media (max-width: 991px) {
        .sign-in {
          white-space: initial;
        }
      }
      .username {
        color: var(--greenDark, #e8f5f1);
        letter-spacing: 2px;
        text-transform: uppercase;
        margin-top: 32px;
        font: 400 12px/32px Roboto, sans-serif;
      }
      .input input {
        flex: 1;
        border: none;
        outline: none;
        background: transparent;
        width: 100%; 
      }

      .input {
        border-radius: var(--4, 4px);
        border: 1px solid var(--greenLight, #d0ffd7);
        background-color: var(--greenLight, #d0ffd7);
        display: flex;
        height: 40px;
        padding-inline: 10px;
        width: 100%; 
      }

      .input input:focus {
        border-color: blue;
      }
      .password {
        color: var(--greenDark, #e8f5f1);
        letter-spacing: 2px;
        text-transform: uppercase;
        margin-top: 4px;
        font: 400 12px/32px Roboto, sans-serif;
      }
      .login {
        color: var(--cardsWhite, #081c15);
        white-space: nowrap;
        justify-content: center;
        align-items: center;
        border-radius: 6px;
        box-shadow: 0px 4px 61px 0px rgba(45, 106, 79, 0.4);
        background-color: var(--cardsMainColor, #f4fbf9);
        margin-top: 32px;
        padding: 11px 60px;
        font: 600 18px Poppins, sans-serif;
      }
      @media (max-width: 991px) {
        .login {
          white-space: initial;
          padding: 0 20px;
        }
      }
      .register-here {
        align-items: center;
        display: flex;
        margin-top: 32px;
        gap: 6px;
      }
      .otherwise {
        color: var(--greenDark, #e8f5f1);
        margin: auto 0;
        font: 400 14px Poppins, sans-serif;
      }
      .register {
        color: var(--greenDark, #e8f5f1);
        font: 600 16px Poppins, sans-serif;
      }
    `,
  ],
})
export class MyComponent {}
